package practice;

public class VariableDeclaration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var_int1 = 10;
		int var_int2 = 20;
		float var_float = 12.5f;
		String var_string = "Java Programing";

		System.out.println("Integer 1 Value: " + var_int1);
		System.out.println("Integer 2 Value: " + var_int2);
		System.out.println("Float Value: " + var_float);
		System.out.println("String Value: " + var_string);

	}

}
